var HEAD = 0
var basementEnter = -1

for instIdx in 0..<InputString.characters.count {
    let instruction = InputString[InputString.startIndex.advancedBy(instIdx)]
    switch instruction {
        case "(":
            HEAD += 1
        case ")":
            HEAD -= 1
        default:
            print("WUT?! \(instruction)")
    }
    if HEAD < 0 && basementEnter == -1{
        basementEnter = instIdx + Int(1)
    }
}

print("We landed on floor \(HEAD) after \(InputString.characters.count) instructions.")
print("We Also entered the basement on instruction \(basementEnter)")
